import Amplify from '@aws-amplify/core';
import { DataStore } from '@aws-amplify/datastore';
import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, Image, SafeAreaView, } from 'react-native';
import { Carbon, Todo } from './src/models';
import awsconfig from './src/aws-exports'
import { Button, Overlay } from 'react-native-elements';
import { constant } from 'async';

Amplify.configure(awsconfig);

const carbon = []

export default function App(){

  const [carbonState, updateCarbon] = useState(carbon);
  useEffect(() => {
    fetchTodo();
    const subscription = DataStore.observe(Carbon).subscribe(() => 
    fetchTodo()
    );
    return () => subscription.unsubscribe();
  });

  async function fetchTodo() {
    const allCarbons = ((await DataStore.query(Carbon)))
    console.log(allCarbons)
    const carbon1 = ((await DataStore.query(Carbon, "50e6be72-9754-4d04-a3ae-3ed876f22221")).Carbon)
    const carbon2 = ((await DataStore.query(Carbon, "60dbabdd-53f5-4a96-9f04-4fc3bf74b6f1")).Carbon)
    const carbon3 = ((await DataStore.query(Carbon, "495ae758-ad65-46ae-bca7-76e4195a438a")).Carbon)
    const carbonState = carbon1 + carbon2 + carbon3
    updateCarbon(carbonState);
  }

  const getCurrentDate=()=>{

    let date = new Date().getDate();
    let month = new Date().getMonth() + 1;
    let year = new Date().getFullYear();

    //Alert.alert(date + '-' + month + '-' + year);
    // You can turn it in to your desired format

    return date + '-' + month + '-' + year;//format: dd-mm-yyyy;
  }

  const OverlayExample = () => {
    const [visible, setVisible] = useState(false);

    const toggleOverlay = () => {
      setVisible(!visible);
    };



  return (

  <SafeAreaView  style={styles.centering}>

    <View>
      <Button title="Open Overlay" onPress={toggleOverlay} />

      <Overlay isVisible={visible} onBackdropPress={toggleOverlay}>
        <Text>Hello from Overlay!</Text>
      </Overlay>
    </View>

    <Image
        style={styles.tinyLogo}
        source={require('./assets/logo.png')}/>

    <View style={styles.container1}>

        <Text>
        My carbon usage:
        </Text>

        <Text >
         {getCurrentDate()}
        </Text>
    </View>




    <View>
        <View style={styles.greyBox}/>
        <Text style={styles.carbonUsage}>
          {carbonState} Kg
        </Text>
    </View>

    <View style={styles.horizontalLine}/>

    <Text style={styles.accounts}>
      ACCOUNTS
    </Text>

    <View style = {styles.container2}> 

      <View style = {styles.container3}> 

              <View style={styles.container4}> 

                <View style={styles.blueBox1}/>

                <Text style={styles.oxiac}>OXI A/C</Text>

              </View>

              <View style={styles.container4}> 

                <View style={styles.greyBox2}/>

                <Text style={styles.oxis}>ø22</Text>

              </View>

      </View>  

      <View style = {styles.container3}>

              <View style={styles.blackline}/> 

              <View style={styles.container4}>
              <View style={styles.blueCircle}/>
              <View style={styles.container8}>
              <Image style={styles.exchangebutton} source={require('./assets/Vector.png')}/>
              </View>
              </View>

              <View style={styles.blackline}/> 

      </View>

      <View style = {styles.container3}> 

          <View style = {styles.container4}> 

            <View style={styles.blueBox2}/>

            <Text style={styles.cashac}>CASH A/C</Text>

          </View>

          <View style = {styles.container4}> 

            <View style={styles.greyBox2}/>

            <Text style={styles.cash}>£323.64</Text>

          </View>

      </View> 

    </View>

    <View style = {styles.container4}>

    <View style={styles.blueBox3}/>
    <Text style={styles.oxipay}>OxiPay</Text>
    </View>

    <View style={styles.horizontalLine}/>

    <View style={styles.container5}>

    <Image style={styles.button} source={require('./assets/bar-chart.jpg')}/>

    <View style={styles.verticalLine}/> 

    <Image style={styles.button} source={require('./assets/home.jpg')}/>

    <View style={styles.verticalLine}/> 

    <Image style={styles.button} source={require('./assets/pay.jpg')}/>

    </View>

  </SafeAreaView>
  )
  }
}

const styles = StyleSheet.create({

  centering: {
    width: 375,
    height: 800,
    position: 'absolute', 
    top: 0, 
    left: 0, 
    right: 0,  
    justifyContent: 'space-between', 
