import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Image, SafeAreaView, } from 'react-native';

import Amplify, { API, graphqlOperation } from 'aws-amplify';
import awsconfig from './src/aws-exports.js';
Amplify.configure(awsconfig);

import { DataStore } from '@aws-amplify/datastore';
import { Carbon } from './src/models';
import { createTodo, updateTodo, deleteTodo } from './graphql/mutations';


export default function App(){;



const todo = { name: "My first todo", description: "Hello world!" };

/* create a todo */
 API.graphql(graphqlOperation(createTodo, {input: todo}));

/* update a todo */
 API.graphql(graphqlOperation(updateTodo, { input: { id: todoId, name: "Updated todo info" }}));

/* delete a todo */
 API.graphql(graphqlOperation(deleteTodo, { input: { id: todoId }}));


  const getCurrentDate=()=>{

    let date = new Date().getDate();
    let month = new Date().getMonth() + 1;
    let year = new Date().getFullYear();

    //Alert.alert(date + '-' + month + '-' + year);
    // You can turn it in to your desired format



    return date + '-' + month + '-' + year;//format: dd-mm-yyyy;
