    console.log("Post saved successfully!");
  } catch (error) {
    console.log("Error saving post", error);
  }

  try {
    const posts = DataStore.query(Carbon.amount);
    console.log("Posts retrieved successfully!", JSON.stringify(posts, null, 2));
